namespace Astro8.Devices;

public class CharacterDevice : IMemoryDevice
{
    private static readonly bool[] CharacterRom;

    private const int FontWidth = 8;
    private const int FontHeight = 8;
    private const int FontSize = FontWidth * FontHeight;

    // No clue why, but it's being done here: https://github.com/sam-astro/Astro8-Computer/blob/0c5b1bc1f998ccc856bc99476546e427847b3bae/Astro8-Emulator/main.cpp#L642-L646
    private const int RenderWidth = 6;
    private const int RenderHeight = 6;

    static CharacterDevice()
    {
        // https://raw.githubusercontent.com/sam-astro/Astro8-Computer/main/char_set_memtape
        const string data = "";

        CharacterRom = new bool[data.Length];

        for (var i = 0; i < data.Length; i++)
        {
            CharacterRom[i] = data[i] == '1';
        }
    }

    private readonly Screen _screen;
    private readonly int _width;
    private readonly int _height;

    public CharacterDevice(Screen screen)
    {
        _screen = screen;
        _width = screen.Width / RenderWidth;
        _height = screen.Height / RenderWidth;
        Length = (int)Math.Ceiling((double) screen.Width / RenderWidth * screen.Height / RenderWidth);
    }

    public int Length { get; }

    public void Initialize(Memory memory, Span<int> span, bool isState)
    {
        for (var i = 0; i < span.Length; i++)
        {
            Write(memory, i, span[i]);
        }
    }

    public void Write(Memory memory, int address, int value)
    {
        var characterOffset = value * FontSize;
        var screenX = address % _width * RenderWidth;
        var screenY = address / _height * RenderHeight;

        for (var i = 0; i < FontSize; i++)
        {
            var x = i % FontWidth;

            if (x >= RenderWidth)
            {
                continue;
            }

            var y = i / FontWidth;

            if (y >= RenderHeight)
            {
                continue;
            }

            var offset = characterOffset + i;

            if (offset < 0 || offset >= CharacterRom.Length)
            {
                continue;
            }

            var color = CharacterRom[offset] ? ScreenColor.White : ScreenColor.Black;
            _screen.WriteOverlay(screenX + x + (screenY + y) * _screen.Width, color.Value);
        }
    }
}
